import styled from "styled-components";
import { useEffect, useState } from "react";

const Container = styled.div`
    width: 100vw;
    height: 100vh;
    display: flex;
    justify-content: center;
    align-items: center;
`;

const Card = styled.div`
    width: 500px;
    height: 300px;
    // 웹페이지 색은 단색 보다는 gradient 가 좋음
    background: linear-gradient(90deg, lightblue, dodgerblue);
    // background: linear-gradient(90deg, #ff7e5f, #feb47b);
    color: white;
    border-radius: 10px; // 모서리 라운드
    display: grid;
    grid-template-columns: 1fr 1fr;
`;

const Icon = styled.div`
    text-align: center;
    img {
        width: 100%;
        margin-top: 20px;
    }
`;

const Weather = styled.div`
    display: flex;
    flex-direction: column;
    text-align: end; // 오른쪽 정렬
`;

const Temp = styled.div`
    margin-top: 20px;
    font-size: 5rem;
    i {
        font-size: 3.5rem;
    }
`;

const City = styled.div`
    font-size: 2.5rem;
`;

const Info = styled.div`
    font-size: 1.5rem;
    margin-top: 30px;
`;

export function OpenWeather() {
    const API_KEY = "b5c0480ce07be15b9bd84a3754bee904";

    const [icon, setIcon] = useState("");
    const [temp, setTemp] = useState(0);
    const [city, setCity] = useState("");
    const [weather, setWeather] = useState("");

    // // useEffect 를 사용 빈 배열 처리를 해야 처음 한번만 처리됨
    // useEffect(() => {
    //     setIcon("@4d");
    //     setTemp(28);
    //     setCity("Daejeon");
    //     setWeather("Clouds");
    // }, []);

    useEffect(() => {
        navigator.geolocation.getCurrentPosition(geoOK, geoError);
    }, []); // 최초 1회만

    function geoOK(position) {
        const lat = position.coords.latitude;
        const lon = position.coords.longitude;
        const url = `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=${API_KEY}&n`;
        fetch(url)
            .then((response) => {
                return response.json();
            })
            .then((data) => {
                console.log(data);
            })
            .catch((error) => {
                console.log("요청이 실패했습니다.", error);
            });
    }

    function geoError() {
        alert("현재 위치정보를 찾을 수 없습니다.");
    }

    return (
        <>
            <Container>
                {/* 왼쪽 아이콘, 오른쪽 위 온도, 아래 도시 */}
                <Card>
                    <Icon>
                        <img
                            src={`https://openweathermap.org/img/wn/${icon}@2x.png`}
                        />
                    </Icon>
                    <Weather>
                        <Temp>
                            {temp}
                            <i className="ti ti-temperature-celsius"></i>
                        </Temp>
                        <City>{city}</City>
                        <Info>{weather}</Info>
                    </Weather>
                </Card>
            </Container>
        </>
    );
}
