import styled from "styled-components";

const Container = styled.div`
    width: 100vw;
    height: 100vh;
    display: flex;
    justify-content: center;
    align-items: center;
`;

const Card = styled.div`
    width: 500px;
    height: 300px;
    // 웹페이지 색은 단색 보다는 gradient 가 좋음
    background: linear-gradient(90deg, lightblue, dodgerblue);
    /* background: linear-gradient(
        90deg,
        #ff7e5f,
        #feb47b
    ); */
    color: white;
    border-radius: 10px; // 모서리 라운드
    display: grid;
    grid-template-columns: 1fr 1fr;
`;

const Icon = styled.div`
    text-align: center;
    img {
        width: 100%;
        margin-top: 20px;
    }
`;

const Weather = styled.div`
    display: flex;
    flex-direction: column;
    text-align: end; // 오른쪽 정렬
`;

const Temp = styled.div`
    margin-top: 20px;
    font-size: 5rem;
    i {
        font-size: 3.5rem;
    }
`;

const City = styled.div`
    font-size: 2.5rem;
`;

const Info = styled.div`
    font-size: 1.5rem;
    margin-top: 30px;
`;

export function OpenWeather() {
    const API_KEY = "b5c0480ce07be15b9bd84a3754bee904";

    const [Icon, setIcon] = useState("");
    const [Temp, setTemp] = useState(0);
    const [City, setCity] = useState("");
    

    return (
        <>
            <Container>
                {/* 왼쪽 아이콘, 오른쪽 위 온도, 아래 도시 */}
                <Card>
                    <Icon>Icon</Icon>
                    <Weather>
                        <Temp>30도</Temp>
                        <City>Daejeon</City>
                        <Info>Rain</Info>
                    </Weather>
                </Card>
            </Container>
        </>
    );
}
