import { useParams } from "react-router-dom";
import { getContentsByLectureId } from "../../../Api/LeaningApi/LeaningApi";
import { useEffect, useState } from "react";
import { getTeacherByLectureId } from "../../../Api/TeacherApi/TeacherApi";
import {
    getLecture,
    getLectureProgressDetails,
} from "../../../Api/LectureApi/LectureApi";
import styled, { keyframes } from "styled-components";

import { LeftSidebar } from "../Sidebar";
import { Navbar } from "../Navbar";

import { Unity, useUnityContext } from "react-unity-webgl";
import axios from "axios";

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

// 240904 수정
const Container = styled.div`
    box-sizing: border-box;
    /* padding: 176px 50px 0 240px; */
    padding: 30px;
    transition: all 0.3s;
    display: grid;
    /* grid-template-columns: 70% 30%; */
    animation: ${fadeIn} 0.6s ease-out;
    width: 100%;
`;
const CourseContentBox = styled.div`
    width: 100%;
    padding: 30px;
`;
const CourseSideMenu = styled.div``;

// 240904 font-size 추가
const Button = styled.button`
    background-color: #007bff;
    color: white;
    padding: 10px 20px;
    border: none;
    border-radius: 5px;
    cursor: pointer;
    margin-top: 5px;
    margin-right: 10px;

    // font-size 추가
    font-size: 12px;

    &:hover {
        background-color: #0056b3;
    }
`;

// 240904 font-size 추가
const PdfLink = styled.a`
    background-color: #28a745;
    color: white;
    padding: 10px 20px;
    border: none;
    border-radius: 5px;
    text-decoration: none;
    cursor: pointer;

    // font-size 추가
    font-size: 12px;

    &:hover {
        background-color: #218838;
    }
`;

const Modal = styled.div`
    position: fixed;
    z-index: 1000;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.5);
    display: flex;
    justify-content: center;
    align-items: center;
`;

const ModalContent = styled.div`
    background-color: #fff;
    padding: 20px;
    border-radius: 10px;
    width: 70%;
    max-width: 800px;
    position: relative;
`;

const CloseButton = styled.button`
    position: absolute;
    top: 10px;
    right: 20px;
    font-size: 30px;
    cursor: pointer;
    background: none;
    border: none;
    color: #fff;
    padding: 0;
    line-height: 1;
    display: flex;
    align-items: center;
    justify-content: center;
    width: 40px;
    height: 40px;
`;
const ContentTitle = styled.p`
    font-size: 24px;
    font-weight: 600;
    margin: 20px 0;
    line-height: 1.4;
    color: #ffce48;
    background-color: transparent;
    border-bottom: 1px outset #fff;
`;

const ContentText = styled.p`
    font-size: 15px;
    color: #fff;
    background-color: transparent;
    line-height: 2;
`;

const LectureImg = styled.img`
    width: 100%;
    border-radius: 12px;
`;

// 스타일 컴포넌트 정의
const Table = styled.table`
    width: 100%;
`;

const TableHead = styled.thead`
    color: white;
`;

const TableRow = styled.tr`
    border-bottom: 1px solid #ddd;

    /* &:nth-child(even) {
    background-color: #f9f9f9;
  }

  &:hover {
    background-color: #f1f1f1;
  } */
`;

const TableHeader = styled.th`
    padding: 12px;
    background-color: #556b2f;
    text-align: center;
`;

const TableData = styled.td`
    padding: 12px;
    color: #fff;
`;

const CourseOption = styled.div`
    background-color: #1c1f27;
    margin-bottom: 20px;
    border-radius: 12px;
    width: 100%;
    padding: 20px;
    position: relative;
`;

const LectureName = styled.p`
    font-size: 24px;
    font-weight: 600;
    margin-bottom: 12px;
    line-height: 1.4;
    color: #ffce48;
    background-color: transparent;
`;

const LectureContentText = styled.p`
    font-size: 15px;
    color: #fff;
    background-color: transparent;
    line-height: 2;
`;

const formatPlaytime = (playtime) => {
    if (playtime.length !== 6) return "00:00:00";
    const hours = playtime.substring(0, 2);
    const minutes = playtime.substring(2, 4);
    const seconds = playtime.substring(4, 6);
    return `${hours}:${minutes}:${seconds}`;
};

export function CourseModal({ userId, lectureId, onClose }) {
    // const { userId, lectureId } = useParams();

    const [learning, setlearing] = useState([]);
    const [teacher, setTeacher] = useState([]);
    const [lecture, setLecture] = useState([]);

    const [progress, setProgress] = useState([]);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [selectedVideoPath, setSelectedVideoPath] = useState("");
    const [remainingDays, setRemainingDays] = useState("");

    let qaUrl = "";

    useEffect(() => {
        Axios();
    }, [lectureId]);

    async function Axios() {
        // learning Content 데이터
        try {
            const learningData = await getContentsByLectureId(lectureId);
            setlearing(learningData);
        } catch (error) {
            console.log("leaning Error", error);
        }

        // Teacher 데이터
        try {
            const teacherData = await getTeacherByLectureId(lectureId);
            setTeacher(teacherData);
