const UserContext = createContext(); // 컨텍스트 생성

export function ReactContext() {
    const [user, setUser] = useState("Tom");

    return (
        <>
            <Component1 />
        </>
    );
}

function Component1() {
    return (
        <>
            <h1>Component1</h1>
            <Component2 />
        </>
    );
}

function Component2() {
    return (
        <>
            <h1>Component2</h1>
            <Component3 />
        </>
    );
}

function Component3({ user }) {
    return (
        <>
            <h1>Component3</h1>
            <Component4 />
        </>
    );
}

function Component4() {
    return (
        <>
            <h1>Component4</h1>
            <Component5 />
        </>
    );
}
