import styled from "styled-components";



const Container = styled.div``;

const Menu = styled.div`
    display: flex;
`;

const Body = styled.div``;

export function BasicRouter() {
    // Router 란 중계기

    const [view, setView] = useState("home");
    function renderView() {
        switch (view) {
            case "home":
                return <Home />;
            case "about":
                return <About />;
            case "contact":
                return <Contact />;
            default:
                return <Home />;
        }
    }

    return (
        <>
            <Container>
                <Menu>
                    <button>HOME</button>
                    <button>ABOUT</button>
                    <button>CONTACT</button>
                </Menu>
                <Body></Body>
            </Container>
        </>
    );
}
