import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { getMovieDetailById, getMovieCreditById, IMG_PATH } from "./api";
import styled from "styled-components";
import { Link } from "react-router-dom";

const Container = styled.div`
    width: 900px;
    display: flex;
    flex-direction: column;
`;
const Header = styled.div`
    margin: 20px;
    color: dodgerblue;
    text-align: center;
    position: relative;
`;
const Back = styled.div`
    position: absolute;
    top: 20px;
    right: 0;
`;
const Img = styled.img`
    width: 100%;
`;
const Content = styled.div`
    font-size: 0.9rem;
    line-height: 30px;
`;

export function Movie() {
    const { id } = useParams(); // Movie 뒤에 movie id 를 추출하여 id 에 넣어 줌
    console.log("id: " + id);

    const [detail, setDetail] = useState(null);
    const [credit, setCredit] = useState(null);

    useEffect(() => {
        getMovieInfo();
    }, []);

    async function getMovieInfo() {
        try {
            let response = await getMovieDetailById(id);
            console.log("getMovieDetailById response: " + response);
            setDetail(response.data);

            response = await getMovieCreditById(id);
            console.log("getMovieCreditById response: " + response);
            setCredit(response.data);
        } catch (error) {
            console.log("Error", error);
        }
    }

    return (
        <>
            {detail && credit && (
            <>
                <Header>
                    <h1>{detail.title}</h1>
                    <Back>
                        <Link to="/movie">BACK</Link>
                    </Back>
                </Header>
                <Img src={IMG_PATH + detail.backdrop_path} />
                <Content>
                    <p>타이틀 : </p>
                    <p>장르 : </p>
                    <p>개봉일 : </p>
                    <p>상영시간 : </p>
                    <p>감독 : </p>
                    <p>배우 : </p>
                    <p>{detail.overview}</p>
                </Content>
            </>
            )}
        </>
    );
}
