import styled from "styled-components";

const Container = styled.div`
    width: 100vw;
    height: 100vh;
    display: flex;
    justify-content: center;
    align-items: center;
`;

const Card = styled.div`
    width: 500px;
    height: 300px;
    // background: linear-gradient(90deg, lightblue, dodgerblue);
    background: linear-gradient(
        90deg,
        #ff7e5f,
        #feb47b
    ); // 웹페이지 색은 단색 보다는 gradient 가 좋음
    color: white;
    border-radius: 10px; // 모서리 라운드
    display: grid;
    grid-template-columns: 1fr 1fr;
`;

const Icon = styled.div`
    text-align: center;
    img {
        width: 100%;
        margin-top: 20px;
    }
`;

const Weather = styled.div`
    display: fl;
`;

const Temp = styled.div``;

const City = styled.div``;

const Info = styled.div``;

export function OpenWeather() {
    const API_KEY = "b5c0480ce07be15b9bd84a3754bee904";
    return (
        <>
            <Container>
                {/* 왼쪽 아이콘, 오른쪽 위 온도, 아래 도시 */}
                <Card>
                    <Icon></Icon>
                    <Weather>
                        <Temp></Temp>
                        <City></City>
                        <Info></Info>
                    </Weather>
                </Card>
            </Container>
        </>
    );
}
