import { useRef } from "react";

export function ReactRef() {
    const inputRef = useRef(null);

    return (
        <>
            <div>
                <input ref={inputRef}></input>
                
                {/* // inputRef 는 객체, inputRef.current 가 */}
                <button onClick={() => inputRef.current.focus()}>
                    입력창에 포커스
                </button>
            </div>
        </>
    );
}
